targets.expr:
histogram_quantile(0.99,
 sum by(instance, le) (rate(kubelet_pod_worker_duration_seconds_bucket{cluster=~"$cluster"}[5m])))
 * on(instance) group_left(node) kubelet_node_name{cluster=~"$cluster"}

histogram_quantile(0.99,
 sum by(instance, le) (rate(kubelet_pod_worker_duration_seconds_bucket{job="kubelet",metrics_path="/metrics",node="$node"}[5m])))
 * on(instance) group_left(node) kubelet_node_name{job="kubelet",metrics_path="/metrics",node="$node"}

sum by (verb)((cluster:apiserver_request_duration_seconds:mean5m{cluster=~"$cluster", job=~"apiserver|kubernetes-apiservers"}
  > on(verb) group_left() (avg by(verb) (cluster:apiserver_request_duration_seconds:mean5m{cluster=~"$cluster", job=~"apiserver|kubernetes-apiservers"}
  >= 0) + 2 * stddev by(verb) (cluster:apiserver_request_duration_seconds:mean5m{cluster=~"$cluster", job=~"apiserver|kubernetes-apiservers"}
  >= 0))) > on(verb) group_left() 1.2 * avg by(verb) (cluster:apiserver_request_duration_seconds:mean5m{cluster=~"$cluster", job=~"apiserver|kubernetes-apiservers"}
  >= 0) and on(verb, resource) cluster_quantile:apiserver_request_duration_seconds:histogram_quantile{cluster=~"$cluster", job=~"apiserver|kubernetes-apiservers",quantile="0.99"})
